import { useState } from "react";

function App() {
  const [form, setForm] = useState({ firstName: "", lastName: "", personalCode: "", email: "" });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:8080/api/customers", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    const result = await response.text();
    setMessage(result);
  };

  return (
    <div>
      <h1>Lisa uus klient</h1>
      <form onSubmit={handleSubmit}>
        <input name="firstName" placeholder="Eesnimi" onChange={handleChange} />
        <input name="lastName" placeholder="Perenimi" onChange={handleChange} />
        <input name="personalCode" placeholder="Isikukood" onChange={handleChange} />
        <input name="email" placeholder="E-post" onChange={handleChange} />
        <button type="submit">Salvesta</button>
      </form>
      <p>{message}</p>
    </div>
  );
}

export default App;
